import React from 'react';
import { motion } from 'framer-motion';
import { AppWrap } from '../../wrapper';
import { images } from '../../constants';
import './About.scss';

const About = () => {
  const abouts = [
    {
      title: 'Modern Design',
      description: 'We create beautiful and modern designs',
      imgUrl: images.about01
    },
    {
      title: 'Product Development',
      description: 'Building high-quality products is our passion',
      imgUrl: images.about02
    },
    {
      title: 'User Experience',
      description: 'We focus on creating the best user experience',
      imgUrl: images.about03
    },
    {
      title: 'Innovation',
      description: 'Always staying ahead with innovative solutions',
      imgUrl: images.about04
    }
  ];

  return (
    <>
      <h2 className="head-text">
        We Know that <span>Good Design</span> <br />means <span>Good Business</span>
      </h2>

      <div className="app__profiles">
        {abouts.map((about, index) => (
          <motion.div
            whileInView={{ opacity: 1 }}
            whileHover={{ scale: 1.1 }}
            transition={{ duration: 0.5, type: 'tween' }}
            className="app__profile-item"
            key={about.title + index}
          >
            <img src={about.imgUrl} alt={about.title} />
            <h2 className="bold-text" style={{ marginTop: 20 }}>{about.title}</h2>
            <p className="p-text" style={{ marginTop: 10 }}>{about.description}</p>
          </motion.div>
        ))}
      </div>
    </>
  );
};

export default AppWrap(About, 'about', 'app__whitebg');
