import React, { useEffect, useState } from 'react';
import { Link, useParams } from "react-router-dom";
import { getDetailProductByIdService, getProductRecommendService } from '../../services/userService';
import ImgDetailProduct from '../../component/Product/ImgDetailProduct';
import InfoDetailProduct from '../../component/Product/InfoDetailProduct';
import CommentProduct from '../../component/Product/CommentProduct';
import ProfileProduct from '../../component/Product/ProfileProduct';
import ReviewProduct from '../../component/Product/ReviewProduct';
import DescriptionProduct from '../../component/Product/DescriptionProduct';
import NewProductFeature from "../../component/HomeFeature/NewProductFeature"
import ProductFeature from '../../component/HomeFeature/ProductFeature';
import { Spinner } from 'react-bootstrap';

function DetailProductPage(props) {
    const [dataProduct, setDataProduct] = useState({});
    const [dataDetailSize, setdataDetailSize] = useState({});
    const { id } = useParams();
    const [user, setUser] = useState({});
    const [dataProductRecommend, setdataProductRecommend] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const initializePage = async () => {
            try {
                setLoading(true);
                const userData = JSON.parse(localStorage.getItem('userData'));
                if (userData) {
                    await fetchProductFeature(userData.id);
                    setUser(userData);
                }
                await fetchDetailProduct();
                window.scrollTo(0, 0);
            } catch (err) {
                setError('Có lỗi xảy ra khi tải thông tin sản phẩm');
            } finally {
                setLoading(false);
            }
        };
        
        initializePage();
    }, [id]);

    let sendDataFromInforDetail = (data) => {
        setdataDetailSize(data);
    };

    let fetchDetailProduct = async () => {
        let res = await getDetailProductByIdService(id);
        if (res && res.errCode === 0) {
            setDataProduct(res.data);
        }
    };

    let fetchProductFeature = async (userId) => {
        let res = await getProductRecommendService({
            limit: 20,
            userId: userId
        });
        if (res && res.errCode === 0) {
            setdataProductRecommend(res.data);
        }
    };

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center min-vh-100">
                <Spinner animation="border" variant="primary" />
            </div>
        );
    }

    if (error) {
        return (
            <div className="alert alert-danger m-5" role="alert">
                {error}
            </div>
        );
    }

    return (
        <div className="product-detail-page">
            <section className="banner_area bg-light">
                <div className="banner_inner py-5">
                    <div className="container">
                        <div className="banner_content d-md-flex justify-content-between align-items-center">
                            <div className="mb-3 mb-md-0">
                                <h2 className="h3 font-weight-bold">{dataProduct.name || 'Chi tiết sản phẩm'}</h2>
                                <p className="text-muted mb-0">Thông số chi tiết về sản phẩm</p>
                            </div>
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb bg-transparent mb-0 p-0">
                                    <li className="breadcrumb-item">
                                        <Link to="/" className="text-muted">Trang chủ</Link>
                                    </li>
                                    <li className="breadcrumb-item">
                                        <Link to="/shop" className="text-muted">Cửa hàng</Link>
                                    </li>
                                    <li className="breadcrumb-item active" aria-current="page">
                                        {dataProduct.name}
                                    </li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </section>

            <div className="product_image_area py-5">
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            <InfoDetailProduct 
                                userId={user?.id || ''} 
                                dataProduct={dataProduct}
                                sendDataFromInforDetail={sendDataFromInforDetail}
                            />
                        </div>
                    </div>
                </div>
            </div>

            <section className="product_description_area mb-5">
                <div className="container">
                    <ul className="nav nav-tabs nav-fill border-bottom mb-4" id="productTabs" role="tablist">
                        <li className="nav-item">
                            <a className="nav-link active px-4" id="profile-tab" data-toggle="tab" href="#profile" role="tab">
                                Thông số chi tiết
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link px-4" id="home-tab" data-toggle="tab" href="#home" role="tab">
                                Mô tả chi tiết
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link px-4" id="review-tab" data-toggle="tab" href="#review" role="tab">
                                Đánh giá
                            </a>
                        </li>
                    </ul>

                    <div className="tab-content" id="productTabContent">
                        <div className="tab-pane fade show active" id="profile" role="tabpanel" aria-labelledby="profile-tab">
                            <ProfileProduct data={dataDetailSize} />
                        </div>
                        <div className="tab-pane fade" id="home" role="tabpanel" aria-labelledby="home-tab">
                            <DescriptionProduct data={dataProduct.contentHTML} />
                        </div>
                        <div className="tab-pane fade" id="review" role="tabpanel" aria-labelledby="review-tab">
                            <ReviewProduct />
                        </div>
                    </div>
                </div>

                {user && dataProductRecommend?.length > 0 && (
                    <div className="container mt-5">
                        <ProductFeature 
                            title="Sản phẩm bạn quan tâm"
                            data={dataProductRecommend}
                        />
                    </div>
                )}
            </section>
        </div>
    );
}

export default DetailProductPage;
