import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import { getAuth, RecaptchaVerifier, signInWithPhoneNumber } from 'firebase/auth';
import { initializeApp } from 'firebase/app';

const firebaseConfig = {
  apiKey: "AIzaSyC6BDR8vZuUHiqt7VQkhLJ3pxYroNNjntA",
  authDomain: "ecom-chat-1d35c.firebaseapp.com",
  projectId: "ecom-chat-1d35c",
  storageBucket: "ecom-chat-1d35c.appspot.com",
  messagingSenderId: "517909678281",
  appId: "1:517909678281:web:ee31dcdc6180e6a3cee518",
  measurementId: "G-3X0P8VH3KR"
};

// Initialize Firebase
firebase.initializeApp(firebaseConfig);
export default firebase;

export const authentication = getAuth(initializeApp(firebaseConfig));
export const auth = getAuth();

// Cấu hình RecaptchaVerifier
export const generateRecaptcha = () => {
  window.recaptchaVerifier = new RecaptchaVerifier('recaptcha-container', {
    'size': 'invisible',
    'callback': (response) => {
      // reCAPTCHA solved
    }
  }, auth);
}

// Helper function to format phone number
const formatPhoneNumber = (phoneNumber) => {
  // Remove any non-digit characters
  const cleaned = phoneNumber.replace(/\D/g, '');
  // If the number starts with 0, replace it with +84
  return cleaned.startsWith('0') ? `+84${cleaned.substring(1)}` : cleaned;
};

// Hàm gửi OTP
export const requestOTP = async (phoneNumber) => {
  try {
    const formattedNumber = formatPhoneNumber(phoneNumber);
    const appVerifier = window.recaptchaVerifier;
    const confirmationResult = await signInWithPhoneNumber(auth, formattedNumber, appVerifier);
    window.confirmationResult = confirmationResult;
    return true;
  } catch (error) {
    console.error(error);
    return false;
  }
}

// Hàm verify OTP
export const verifyOTP = async (otp) => {
  try {
    const confirmationResult = window.confirmationResult;
    const result = await confirmationResult.confirm(otp);
    return result.user;
  } catch (error) {
    console.error(error);
    return null;
  }
}
