import React, { useEffect, useState } from 'react';
import { Link, useParams } from "react-router-dom";
import { getDetailProductByIdService, getProductRecommendService } from '../../services/userService';
import ImgDetailProduct from '../../component/Product/ImgDetailProduct';
import InfoDetailProduct from '../../component/Product/InfoDetailProduct';
import CommentProduct from '../../component/Product/CommentProduct';
import ProfileProduct from '../../component/Product/ProfileProduct';
import ReviewProduct from '../../component/Product/ReviewProduct';
import DescriptionProduct from '../../component/Product/DescriptionProduct';
import NewProductFeature from "../../component/HomeFeature/NewProductFeature";
import ProductFeature from '../../component/HomeFeature/ProductFeature';

function DetailProductPage(props) {
    const [dataProduct, setDataProduct] = useState({});
    const [dataDetailSize, setDataDetailSize] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const { id } = useParams();
    const [user, setUser] = useState({});
    const [dataProductRecommend, setDataProductRecommend] = useState([]);

    useEffect(() => {
        const initializeData = async () => {
            try {
                setIsLoading(true);
                const userData = JSON.parse(localStorage.getItem('userData'));
                if (userData) {
                    await Promise.all([
                        fetchProductFeature(userData.id),
                        fetchDetailProduct()
                    ]);
                    setUser(userData);
                } else {
                    await fetchDetailProduct();
                }
                window.scrollTo(0, 0);
            } catch (err) {
                setError(err.message);
                console.error('Error loading product details:', err);
            } finally {
                setIsLoading(false);
            }
        };

        initializeData();
    }, [id]);

    const sendDataFromInforDetail = (data) => {
        setDataDetailSize(data);
    };

    const fetchDetailProduct = async () => {
        try {
            const res = await getDetailProductByIdService(id);
            if (res && res.errCode === 0) {
                setDataProduct(res.data);
            } else {
                throw new Error('Failed to fetch product details');
            }
        } catch (err) {
            throw err;
        }
    };

    const fetchProductFeature = async (userId) => {
        try {
            const res = await getProductRecommendService({
                limit: 20,
                userId: userId
            });
            if (res && res.errCode === 0) {
                setDataProductRecommend(res.data);
            } else {
                throw new Error('Failed to fetch product recommendations');
            }
        } catch (err) {
            throw err;
        }
    };

    if (isLoading) return (
        <div className="loading-spinner-container">
            <div className="loading-spinner"></div>
        </div>
    );
    if (error) return (
        <div className="error-container">
            <div className="alert alert-danger" role="alert">
                Error: {error}
            </div>
        </div>
    );

    return (
        <div className="detail-product-container">
            <section className="banner_area">
                <div className="banner_inner d-flex align-items-center">
                    <div className="container">
                        <div className="banner_content d-md-flex justify-content-between align-items-center">
                            <div className="mb-3 mb-md-0">
                                <h2 className="banner-title">Chi tiết sản phẩm</h2>
                                <p className="banner-subtitle">
                                    Thông số chi tiết về sản phẩm
                                </p>
                            </div>
                            <div className="page_link">
                                <Link to={"/"} className="link-item">Trang chủ</Link>
                                <Link to={"/shop"} className="link-item">Cửa hàng</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            <div className="product_image_area section_padding">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <ImgDetailProduct data={dataProduct} />
                        </div>
                        <div className="col-lg-6">
                            <InfoDetailProduct 
                                userId={user?.id || ''} 
                                dataProduct={dataProduct} 
                                sendDataFromInforDetail={sendDataFromInforDetail}
                            />
                        </div>
                    </div>
                </div>
            </div>

            {user && dataProductRecommend?.length > 0 && (
                <section className="recommended_products section_padding">
                    <ProductFeature 
                        title={"Sản phẩm bạn quan tâm"} 
                        data={dataProductRecommend}
                    />
                </section>
            )}

            <section className="product_description_area section_padding">
                <div className="container">
                    <ul className="nav nav-tabs custom-tabs" id="myTab" role="tablist">
                        <li className="nav-item">
                            <a className="nav-link active" id="profile-tab" data-toggle="tab" href="#profile"
                                role="tab" aria-controls="profile" aria-selected="false">
                                <span className="tab-text">Thông số chi tiết</span>
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" id="home-tab" data-toggle="tab" href="#home" 
                                role="tab" aria-controls="home" aria-selected="true">
                                <span className="tab-text">Mô tả chi tiết</span>
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" id="review-tab" data-toggle="tab" href="#review"
                                role="tab" aria-controls="review" aria-selected="false">
                                <span className="tab-text">Đánh giá</span>
                            </a>
                        </li>
                    </ul>
                    <div className="tab-content custom-tab-content" id="myTabContent">
                        <div className="tab-pane fade show active" id="profile" role="tabpanel" aria-labelledby="profile-tab">
                            <ProfileProduct data={dataDetailSize} />
                        </div>
                        <div className="tab-pane fade" id="home" role="tabpanel" aria-labelledby="home-tab">
                            <DescriptionProduct data={dataProduct.contentHTML} />
                        </div>
                        <div className="tab-pane fade" id="review" role="tabpanel" aria-labelledby="review-tab">
                            <ReviewProduct />
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
}

export default DetailProductPage;
